      {showMessage && (
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 2 }}
          className="text-xl mt-4"
        >
          Wishing you a day filled with love, joy, and endless surprises! ðŸ’–
        </motion.p>
      )}
    </div>
  );
};

export default BirthdaySurprise;
